root.script:
function onInit()
    if ExtensionManagerSizeChanges.shouldLoadEffects() then
        EditorManager.addEditor(
            "category_misc",
            {
                value="SIZE",
                labelres="effect_size",
                windowclass="effect_SIZE"
            }
        )
        EditorManager.addEditor(
            "category_misc",
            {
                value="ESIZE",
                labelres="effect_esize",
                windowclass="effect_SIZE"
            }
        )
    end
end
